              content: 'Para visualizar motoristas cadastrados e cadastra-los.',
              params: {
                placement: 'bottom'
              }
            },
            {
              target: '[data-v-step="4"]',
              content: 'Para viasualizar e cadastrar novos passageiros',
              params: {
                placement: 'bottom'
              }
            },
            {
              target: '[data-v-step="5"]',
              content: 'atualizar a página',
              params: {
                placement: 'bottom'
              }
            },
            {
              target: '[data-v-step="6"]',
              content: 'adicionar novo motorista',
              params: {
                placement: 'bottom'
              }
            },
          ]
        }
      },
     methods:{
        iniciarTour(){
            this.$tours['myTour'].start()
        },

        paginaMotorista(currentStep) {      
            if (currentStep === 2) {
              this.$router.push('/Motoristas')
            }
        }
    },
    // mounted: function () {
