import axios from 'axios'
export default {
   data: () => ({
      dialog: false,
      headers: [
        { text: 'Nome',                align: 'left', value: 'nome',       sortable: false},
        { text: 'CPF',                 align: 'left', value: 'cpf',        sortable: false },
        { text: 'Data de Nascimento',  align: 'left', value: 'nascimento', sortable: false },
        { text: 'Sexo',                align: 'left', value: 'sexo',       sortable: false },
        { text: 'Ações',                              value: 'acoes',      sortable: false }
      ],

      listapassageiros: [],
      editarIndex: -1,
      passageiro: {
        nome: '',
        cpf: '',
        nascimento: '',
        sexo: null,
      },
      defaultItem: {
        nome: '',
        cpf: null,
        nascimento: null,
        sexo: null,
      },

      //validação
      obrigatorio: [
        v => !!v || 'Campo Obrigatorio'
      ],
      
      documentoValido:[
        v => !!v || 'Campo Obrigatorio',
        v => v.length == 14 || 'Insira um CPF Valido'
      ],
      dataValida:[
        v => !!v || 'Campo Obrigatorio',
        v => v.length == 10 || 'Insira a data Corretamente'
      ],
    
       //snakebar
      snackbar: false,
      color: '',
      mode: '',
      timeout: 3000,
      text: 'Salvo com Sucesso'
    }),

    computed: {
      formTitle () {
        return this.editarIndex === -1 ? 'Adicionar passageiro' : 'Editar passageiro'
      }
    },

    watch: {
      dialog (val) {
        val || this.fechar()
      }
    },

    created () {
      this.inicializando()
    },

    methods: {
      inicializando () {
       let url = 'http://localhost:8081/passageiros'
        axios
          axios.get(url, 
          { 
            headers: {
              'content-type': 'application/json',
              'Accept': 'application/json',
              'Access-Control-Allow-Credentials':true,
              'Access-Control-Allow-Origin':true
           }})
          .then(response => {
            this.listapassageiros = response.data.response
            console.log(response.data)
          })
      .catch(error => {
        console.log(error)
        this.errored = true
      })
      },

      editarItem (item) {
        this.editarIndex = this.listapassageiros.indexOf(item)
        this.passageiro = Object.assign({}, item)
        this.dialog = true
      },

      excluirItem (item) {
        let index = this.listapassageiros.indexOf(item)
        confirm('Tem certeza que deseja excluir esse passageiro?') && this.listapassageiros.splice(index, 1)
      },

      fechar () {
        this.dialog = false
        setTimeout(() => {
          this.$refs.form.resetValidation()
          this.passageiro = Object.assign({}, this.defaultItem)
          this.editarIndex = -1
        }, 300)
      },

      salvar () {
        if(this.$refs.form.validate()){
          if (this.editarIndex > -1) {
            Object.assign(this.listapassageiros[this.editarIndex], this.passageiro)
          } else {

            this.passageiro.nascimento = this.formataDataParaBanco(this.passageiro.nascimento)
            this.passageiro.cpf = this.formatarCpf(this.passageiro.cpf)
            axios.post('http://localhost:8081/passageiros', this.passageiro)
              .then(function(response){
                  this.snackbar = true
                  this.inicializando()
            });
            
          }
          this.fechar()
        }
      },
      formatarCpf(cpf){
        return cpf.replace(/\D/g, '')
      },

       formataDataParaBanco(data){
        var dia  = data.split("/")[0];
        var mes  = data.split("/")[1];
        var ano  = data.split("/")[2];
        return ano + '-' + ("0"+mes).slice(-2) + '-' + ("0"+dia).slice(-2)
      },

      formatarData(data){
        let configData = data.split("-");
        let dataFormatada =
          configData[2].split("T")[0] +
          "/" +
          configData[1] +
          "/" +
          configData[0];
        return dataFormatada;
      },

    }

}
